and kvbatch_history.data_source = 'KV_Production'
ORDER BY kvbatch_history.updated_date desc;


WHERE batch_id in (58,57,56, 59, 60, 61) and kvbatch_history.updated_date >= now()::date + interval '1h'
and kvbatch_history.data_source = 'Pelita_Test'
ORDER BY kvbatch_history.updated_date desc;


WHERE batch_id in (58,57,56, 59, 60, 61) and kvbatch_history.updated_date >= now()::date + interval '1h'
and kvbatch_history.data_source = 'KV_Production'
ORDER BY kvbatch_history.updated_date desc;


WHERE batch_id in (62) and kvbatch_history.updated_date >= now()::date + interval '1h'
and kvbatch_history.data_source = 'Pelita_Test'
ORDER BY kvbatch_history.updated_date desc;


