mapper.select:
select stage.doc_type as doc_type, stage.assignment as assignment, stage.clear_doc as clear_doc, stage.trx_mode as trx_mode, inv.load_id as load_id,
case when stage.trx_mode = 'H' then 'INVOICE' ELSE 'CREDIT' end as DOC_TYPE_CODE, case when stage.trx_mode = 'H' then stage.assignment ELSE stage.clear_doc end as INVOICE_NO_DERIVED
from vendor_invoice_load stage where stage.doc_type in ('AA', 'AB', 'GA');

mapper.update:
select
case when stage.trx_mode = 'H' then stage.assignment ELSE stage.clear_doc end as invoice_no, case when stage.trx_mode = 'H' then 'INVOICE' ELSE 'CREDIT' end as line_of_business, case when stage.trx_mode = 'H' then stage.assignment ELSE stage.clear_doc end as payment_reference
from vendor_invoice_load stage where stage.doc_type in ('AA', 'AB', 'GA')
