mapper.update:
select
'OTHERS' as DOC_TYPE_CODE, case when (stage.clear_doc = null or stage.clear_doc = '') then stage.assignment else stage.clear_doc end as INVOICE_NO_DERIVED
from vendor_invoice_load stage where stage.doc_type not in ('AA','AB','GA','PB','PC','PD','PP','Y3','G1','G2','GC','GD','GE','GH','GL','GR','GV','NA','NI','NJ','NK', 'NM','PH','PS','PL','RE','RB','RC','RD','RG','RH','RM','RS','Y6','Y7','YM','ZP','ZV','ZZ');
