mapper.select:
select stage.doc_type as doc_type, stage.assignment as assignment, stage.clear_doc as clear_doc, stage.trx_mode as trx_mode, inv.load_id as load_id,
case when stage.trx_mode = 'H' then 'INVOICE' ELSE 'PAYMENT' end as DOC_TYPE_CODE, case when stage.trx_mode = 'H' then stage.assignment ELSE stage.clear_doc end as INVOICE_NO_DERIVED
from vendor_invoice_load stage left join kv_vendor_invoices inv on stage.id = inv.load_id where stage.doc_type in ('GC', 'GD');

mapper.insert:
insert into kv_vendor_invoices (doc_type, assignment, clear_doc, trx_mode, load_id, doc_type_code, invoice_no) select stage.doc_type as doc_type, stage.assignment as assignment, stage.clear_doc as clear_doc, stage.trx_mode as trx_mode, inv.load_id as load_id,
case when stage.trx_mode = 'H' then 'INVOICE' ELSE 'PAYMENT' end as DOC_TYPE_CODE, case when stage.trx_mode = 'H' then stage.assignment ELSE stage.clear_doc end as INVOICE_NO_DERIVED
from vendor_invoice_load stage left join kv_vendor_invoices inv on stage.id = inv.load_id where stage.doc_type in ('GC', 'GD') on CONFLICT (load_id) DO UPDATE set invoice_no = EXCLUDED.invoice_no, load_id = EXCLUDED.load_id, doc_type = EXCLUDED.doc_type, assignment = EXCLUDED.assignment, clear_doc = EXCLUDED.clear_doc, trx_mode = EXCLUDED.trx_mode, doc_type_code = EXCLUDED.doc_type_code;
