mapper.select:
select stage.doc_type AS doc_type, stage.assignment AS assignment, stage.clear_doc AS clear_doc, stage.trx_mode AS trx_mode, inv.load_id AS load_id,
case when stage.trx_mode = 'H' then 'INVOICE' else 'PAYMENT' end as DOC_TYPE_CODE, case when stage.trx_mode = 'H' then stage.assignment else case when (stage.clear_doc = null or stage.clear_doc = '') then stage.assignment else clear_doc end end as INVOICE_NO_DERIVED
from vendor_invoice_load stage left join kv_vendor_invoices inv on stage.id = inv.load_id where stage.doc_type in ('GH', 'RC', 'RD','RG','G2','ZZ');
